INSERT INTO `mf_goods` VALUES (47, '', 22, '无培养基拭子', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1491891589.jpg', '', '', '', '', '', '<div>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:18px;\">【产品性能】管体透明，可见管体内棉签，且无肉眼可见的杂物，棉头柔软富有弹性、白色、无色斑、无污点、无异味；棉杆光滑、与皮肤接触没有毛刺感。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【产品结构】</span><span style=\"font-size:18px;\">YP/S0000(</span><span style=\"font-size:18px;\">无培养基）主要由管体、盖子、人造纤维棉头以及棉签杆组成。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【适用范围】</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 1. </span></span><span style=\"font-size:18px;\">可用于人体多部位的微生物样品收集、运送：眼耳鼻咽喉部、各种伤口分泌物、肛肠、泌尿生殖器分泌物等。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 2. </span></span><span style=\"font-size:18px;\">妇女白带检查标本采集：适用于白带常规检验和其他病原微生物检查，如：培养、</span><span style=\"font-size:18px;\">PCR</span><span style=\"font-size:18px;\">（保持微生物存活、避免荧光物质污染等）。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 3. </span></span><span style=\"font-size:18px;\">检验检疫采样（海关、</span><span style=\"font-size:18px;\">CDC</span><span style=\"font-size:18px;\">）、畜牧业检疫采样。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【使用说明】</span><span> <br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 1. </span></span><span style=\"font-size:18px;\">在单包装袋撕裂指示处撕开单包装。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 2. </span></span><span style=\"font-size:18px;\">管体、管盖逆向旋转，拧断标贴并取出带棉签的盖子。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 3. </span></span><span style=\"font-size:18px;\">在病灶部位收集微生物标本。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 4. </span></span><span style=\"font-size:18px;\">把收集了标本的棉签连同盖子一起插入管体内，盖牢。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 5. </span></span><span style=\"font-size:18px;\">送检，原则上要求及时送检、及时处理，不得保存。</span><span></span> \r\n	</p>\r\n<span style=\"font-size:18px;\"></span><br />\r\n</div>', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">规格型号：</span><span><span style=\"font-size:18px;\">YP/S0000</span><br />\r\n</span><span style=\"font-size:18px;\">棉头材质：人造纤维棉</span><span><br />\r\n</span><span style=\"font-size:18px;\">产品材质：杆体为</span><span style=\"font-size:18px;\">PS</span><span style=\"font-size:18px;\">、管体及管盖材质为医用</span><span><span style=\"font-size:18px;\">PP</span><br />\r\n</span><span style=\"font-size:18px;\">产品尺寸：管体为</span><span style=\"font-size:18px;\">12mm</span><span style=\"font-size:18px;\">（直径）</span><span style=\"font-size:18px;\">*170mm</span><span style=\"font-size:18px;\">（长度）、棉签总长度为</span><span style=\"font-size:18px;\">146.5mm</span><span style=\"font-size:18px;\">±</span><span style=\"font-size:18px;\">0.5mm</span><span style=\"font-size:18px;\">、棉头最大直径为</span><span style=\"font-size:18px;\">5.0mm</span><span style=\"font-size:18px;\">±</span><span style=\"font-size:18px;\">0.2mm</span><span style=\"font-size:18px;\">、棉头总长度为</span><span style=\"font-size:18px;\">13.5mm</span><span style=\"font-size:18px;\">±</span><span><span style=\"font-size:18px;\">1.0mm</span><br />\r\n</span><span style=\"font-size:18px;\">包装规格：</span><span style=\"font-size:18px;\">50</span><span style=\"font-size:18px;\">支</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">盒、</span><span style=\"font-size:18px;\">500</span><span style=\"font-size:18px;\">支</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">箱</span><span></span> \r\n</p>', '<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170711/20170711150129_16124.pdf\" target=\"_blank\">微生物采集转运系统</a>', '', '', '微生物采集转运系统', 2, 1, 1499756502, '', 21, '', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (61, '', 29, 'GNT医院智能采血管理系统', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1493088403.png', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:18px;\"><strong>项目适用范围</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;font-family:宋体;\">医院智能采血管理系统是一个以防止血液标本差错、提升病人满意度、避免医患纠纷为目的，打造国际标准化采血流程，实现实验室标本分析前质量控制的智能化解决方案。是以采血管贴标分配系统为核心，排队管理系统为辅助，再结合为医院本身已有的</span><span style=\"font-size:18px;\">HIS/LIS</span><span style=\"font-size:18px;font-family:宋体;\">系统所准备的数据接口系统所组成，该项目适用于门诊、体检中心等。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:18px;\">系统组成</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;\">GNT</span><span style=\"font-size:18px;font-family:宋体;\">医院智能采血管理系统由</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;font-family:宋体;\">病人自助登记排队系统</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;font-family:宋体;\">、</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;font-family:宋体;\">中央调度系统</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;font-family:宋体;\">、</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;font-family:宋体;\">贴标分管模块</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;font-family:宋体;\">、</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;font-family:宋体;\">试管运送模块</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;font-family:宋体;\">、</span><span style=\"font-size:18px;\">“4-6</span><span style=\"font-size:18px;font-family:宋体;\">个采血工作台</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;font-family:宋体;\">组成。</span> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/attached/image/20170706/20170706112945_32143.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:18px;line-height:1.5;\"><strong>产品优势</strong></span> \r\n</p>\r\n<h3 style=\"text-indent:2em;\">\r\n	<span style=\"font-weight:normal;font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-weight:normal;font-size:18px;line-height:150%;font-family:宋体;\">加载试管更方便，原包装直接上机</span> \r\n</h3>\r\n<h3 style=\"text-indent:2em;\">\r\n	<span style=\"font-weight:normal;font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-weight:normal;font-size:18px;line-height:150%;font-family:宋体;\">备管速度快</span> \r\n</h3>\r\n<h3 style=\"text-indent:2em;\">\r\n	<span style=\"font-weight:normal;font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-weight:normal;font-size:18px;line-height:150%;font-family:宋体;\">机械手取管方式，更安全</span> \r\n</h3>\r\n<h3 style=\"text-indent:2em;\">\r\n	<span style=\"font-weight:normal;font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-weight:normal;font-size:18px;line-height:150%;font-family:宋体;\">采血桌更符合人体工学设计</span> \r\n</h3>\r\n<h3 style=\"text-indent:2em;\">\r\n	<span style=\"font-weight:normal;font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-weight:normal;font-size:18px;line-height:150%;font-fam