INSERT INTO `mf_goods` VALUES (62, '', 29, 'HENs医院智能采血管理系统', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1493088936.jpg', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:18px;\">项目适用范围</span></strong>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;font-family:宋体;\">医院智能采血管理系统是一个以防止血液标本差错、提升病人满意度、避免医患纠纷为目的，打造国际标准化采血流程，实现实验室标本分析前质量控制的智能化解决方案。是以采血管贴标分配系统为核心，排队管理系统为辅助，再结合为医院本身已有的</span><span style=\"font-size:18px;\">HIS/LIS</span><span style=\"font-size:18px;font-family:宋体;\">系统所准备的数据接口系统所组成，该项目适用于门诊、体检中心、急诊、护士站等。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"><strong>系统组成</strong></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;\">HENs</span><span style=\"font-size:18px;\">医院智能采血管理系统由</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;\">病人自助登记排队系统</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">“</span><span style=\"font-size:18px;\">中央调度系统</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">“HENS</span><span style=\"font-size:18px;\">智能选管贴标系统</span><span style=\"font-size:18px;\">”</span><span style=\"font-size:18px;\">组成。</span>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/attached/image/20170706/20170706113740_45298.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:1.5;\"><strong>产品优势</strong></span>\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:12pt;line-height:150%;font-family:Wingdings;\"><span style=\"font-size:18px;\">Ø</span><span style=\"font-size:18px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp; </span></span><span style=\"font-size:18px;line-height:150%;font-family:宋体;\">加载试管更方便，原包装直接上机</span>\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font