INSERT INTO `mf_goods` VALUES (84, '', 30, '降钙素原检测试剂盒', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1498806397.jpg', '', '', '', '', '', '<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">通用名称：降钙素原检测试剂盒（干式免疫荧光定量法）</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">英文名称：</span><span style=\"font-size:18px;line-height:2;\">PCT test</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【预测用途】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">用于体外定量检测人全血、血清、血浆样本中降钙素原的浓度。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【检测原理】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">本产品采用免疫荧光定量检测技术、免疫层析法。用于检测人体全血、血浆样本中降钙素原的含量。将血液样本和样品缓冲液在缓冲液瓶中进行混合，缓冲液中的荧光标记抗降钙素原抗体和血液中的降钙素原抗原结合形成抗原抗体复合物。当混合物样本被加入到检测卡的加样孔后，在层析作用下沿着硝酸纤维素膜向前扩散，该复合物被固定在检测线上的降钙素原抗体捕获，血液样本中的降钙素原越多，检测线上聚集的复合物越多，荧光抗体的信号强度反映了被捕获的降钙素原数量，通过干式免疫荧光定量分析仪处理，能够检测血液样本中降钙素原的浓度。分析仪默认的降钙素原检测结果用</span><span style=\"font-size:18px;line-height:2;\">ng/mL</span><span style=\"font-size:18px;line-height:2;\">做单位。</span> \r\n</p>', '<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品参数】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">简单快速：操作简单，检测只需</span><span style=\"font-size:18px;line-height:2;background-color:#FFFFFF;\">15min</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">线性范围宽：</span><span style=\"font-size:18px;line-height:2;background-color:#FFFFFF;\">0.10-100ng/mL</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">正常参考值：＜</span><span style=\"font-size:18px;line-height:2;background-color:#FFFFFF;\">0.10ng/mL</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">精密度：批内</span><span style=\"font-size:18px;line-height:2;background-color:#FFFFFF;\">CV≤15%；批间CV≤20%</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">样本类型：全血</span><span style=\"font-size:18px;line-height:2;background-color:#FFFFFF;\">100μL，血清/血浆90μL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">包装规格：</span><span style=\"font-size:18px;line-height:2;\">25人份/盒、50人份/盒</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170630/20170630145833_96294.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">降钙素原检测试剂盒（正面）</span></a> \r\n</p>\r\n<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170630/20170630150610_18431.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">降钙素原检测试剂盒（反面）</span></a> \r\n</p>', '', '', '降钙素原（PCT）', 2, 1, 1501553476, '', 50, '降钙素原（PCT）是全球医学界公认的细菌感染高特异性标志物，也是脓毒血症快速诊断的高特异性生物标志物。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (85, '', 30, 'D-二聚体检测试剂盒', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1498807275.jpg', '', '', '', '', '', '<p>\r\n	<span style=\"font-size:18px;line-height:2;\">D-二聚体（D-Dimer）是纤维蛋白单体经过活化因子</span><span style=\"font-size:18px;line-height:2;\">Ⅲ交联后，再经纤溶酶水解所产生的一种特异性降解产物，是一个特异性的纤溶过程标记物。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品名称】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">通用名称：</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体检测试剂（干式免疫荧光定量法）</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">英文名称：</span><span style=\"font-size:18px;line-height:2;\">D-Dimer test</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【预测用途】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">用于检测人体全血、血浆样本中</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体的含量。临床上主要用于排除静脉血栓形成、弥散性血管内凝血的辅助诊断及溶栓治疗的检测。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【检测原理】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">本产品采用免疫荧光定量检测技术、免疫层析法。用于检测人体全血、血浆样本中</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体的含量。将血液样本和样品缓冲液在缓冲液瓶中进行混合，缓冲液中的荧光标记抗</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体抗体和血液中的</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体抗原结合形成抗原抗体复合物。当混合物样本被加入到检测卡的加样孔后，在层析作用下沿着硝酸纤维素膜向前扩散，该复合物被固定在检测线上的</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体抗体捕获，血液样本中的</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体越多，检测线上聚集的复合物越多，荧光抗体的信号强度反映了被捕获的</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体数量，通过干式免疫荧光定量分析仪处理，能够检测血液样本中</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体的浓度（单体当量：</span><span style=\"font-size:18px;line-height:2;\">FEU</span><span style=\"font-size:18px;line-height:2;\">）。分析仪默认的</span><span style=\"font-size:18px;line-height:2;\">D-</span><span style=\"font-size:18px;line-height:2;\">二聚体检测结果用</span><span style=\"font-size:18px;line-height:2;\">mg/L FEU</span><span style=\"font-size:18px;line-height:2;\">做单位。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品参数】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:2;\">简单快速：操作简单，检测只需</span><span style=\"font-size:18px;line-height:2;\">10min</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:2;\">线性范围宽：</span><span style=\"font-size:18px;line-height:2;\">0.10-20mg/L FEU</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:2;\">正常参考值：＜</span><span style=\"font-size:18px;line-height:2;\">0.5mg/L FEU</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:2;\">精密度：批内</span><span style=\"font-size:18px;line-height:2;\">CV≤15%；批间CV≤20%</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:2;\">样本类型：全血</span><span style=\"font-size:18px;line-height:2;\">20μL，血浆15μL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">包装规格：</span><span style=\"font-size:18px;line-height:2;\">25人份/盒、50人份/盒</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span> \r\n</p>', '<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170630/20170630151943_47406.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">D-二聚体检测试剂盒（正面）</span></a> \r\n</p>\r\n<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170630/20170630152046_59111.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">D-二聚体检测试剂盒（反面）</span></a> \r\n</p>', '', '', 'D-二聚体（D-Dimer）', 2, 1, 1501553493, '', 50, '用于排除静脉血栓形成、弥散性血管内凝血的辅助诊断及溶栓治疗的检测。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (78, '', 32, '游离DNA保存管', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1497427926.jpg', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><span style=\"font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;血浆保存管是一种直接抽取全血的，常温下可稳定保存游离</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">（</span><span style=\"font-size:18px;line-height:1.5;\">cfDNA</span><span style=\"font-size:18px;line-height:1.5;\">）和循环肿瘤</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">（</span><span style=\"font-size:18px;line-height:1.5;\">ctDNA</span><span style=\"font-size:18px;line-height:1.5;\">），稳定储存和运输的采血管。</span></span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><span style=\"font-size:18px;line-height:1.5;\">虽然健康人体的血浆中只有少量无细胞</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">，但血液循环中的无细胞</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">水平的增减与许多临床疾病有关。</span></span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><br />\r\n</span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><span style=\"font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;由于安全性、准确性而被接受和认可的产妇产前无创筛查即是通过对产妇血液中的胎儿游离</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">的检测分析，对胎儿遗传疾病做出诊断。而对于人体血液中的循环肿瘤</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">的检测分析，为肿瘤早筛创造了条件。同时使针对肿瘤的精准医疗成为可能。</span></span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><br />\r\n</span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><span style=\"font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;血浆保存管中所含的抗凝血剂和细胞防腐剂能稳定有核血细胞，防止释放细胞基因组</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">，抑制无细胞</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">的核酸酶介导降解，有助于无细胞</span><span style=\"font-size:18px;line-height:1.5;\">DNA</span><span style=\"font-size:18px;line-height:1.5;\">的整体稳定。</span></span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><br />\r\n</span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"><span style=\"font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;血浆保存管是亦可稳定保存循环肿瘤细胞（</span><span style=\"font-size:18px;line-height:1.5;\">CTC</span><span style=\"font-size:18px;line-height:1.5;\">）。</span></span><span style=\"font-family:Calibri;line-height:150%;font-size:12.0000pt;\"></span> \r\n</p>', '<p style=\"text-align:center;\">\r\n	<img src=\"/Uploads/attached/image/20170702/20170702123426_63304.png\" alt=\"\" width=\"882\" height=\"103\" title=\"\" align=\"\" /> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<br />\r\n</p>', '<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170824/20170824104354_98725.pdf\" target=\"_blank\">游离DNA保存管</a><br />', '', '', '游离DNA保存管', 2, 1, 1503542774, '', 9, '常温下保存游离胎儿DNA(cffDNA)、循环肿瘤游离DNA（ctDNA)和循环肿瘤细胞5-14天。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (79, '', 30, '干式免疫荧光定量分析仪QT-100', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1497438390.jpg', '', '', '', '', '', '<p>\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>仪器特点</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:2;\">简单快速：一键完成检测流程，3-5秒即可完成测试</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:2;\">使用方便：独特双模式测试，人性化操作（Auto test 和 Normal test）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:2;\">通讯接口：支持LIS连接、电脑连接，外置打印机</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:2;\">应用范围：广泛应用于各类医院的检验科、心内科、急诊科、ICU、肿瘤科、肾内科、儿科、内分泌科、妇科、老年科、呼吸科、胸外科、消化科、泌尿外科等科室</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '<span style=\"font-size:18px;\"> \r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<strong><span style=\"font-size:18px;line-height:2;\">产品参数</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">尺寸：长</span><span style=\"font-size:18px;line-height:2;\">28.5×宽19.5×高10cm</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">重量：</span><span style=\"font-size:18px;line-height:2;\">1.56kg</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">数据输出：液晶显示屏/</span><span style=\"font-size:18px;line-height:2;\">打印机</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">打印机：外接热源打印机</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">液晶显示屏：</span><span style=\"font-size:18px;line-height:2;\">LCD</span> \r\n</p>\r\n</span>', '<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170614/20170614190606_77995.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">QT100彩页.pdf</span></a>', '', '', '', 2, 1, 1501550692, '', 49, '广泛应用于各类医院的检验科、心内科、急诊科、ICU、肿瘤科、肾内科、儿科、内分泌科、妇科、老年科、呼吸科、胸外科、消化科、泌尿外科等科室。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (80, '', 33, '末梢血采集管', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1499240775.jpg', '', '', '', '', '', '<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;\">末梢采集管主要用于采集、盛装、预处理和运输末梢血样本，适用于新生儿、婴幼儿、重症监护的衰竭病人以及其他不适合静脉采血（如严重烧伤）的医患人员，也适用于盛装量较小（一般在1ml以下）的静脉血标本。它由不易破碎、高透明的PP塑料材质制成。</span> \r\n</p>', '<div style=\"text-align:center;\">\r\n	<img src=\"/Uploads/attached/image/20170911/20170911184647_49876.png\" alt=\"\" width=\"1146\" height=\"779\" title=\"\" align=\"\" /> \r\n</div>', '<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170615/20170615140629_30175.pdf\" target=\"_blank\">末梢血采集管</a>', '', '', '末梢血采集管', 2, 1, 1505126813, '', 19, '末梢采集管主要用于采集、盛装、预处理和运输末梢血样本，适用于新生儿、婴幼儿、重症监护的衰竭病人以及其他不适合静脉采血（如严重烧伤）的医患人员，也适用于盛装量较小（一般在1ml以下）的静脉血标本。它由不易破碎、高透明的PP塑料材质制成。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (81, '', 30, 'N-末端脑利钠肽前体检测试剂盒', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1498726476.jpg', '', '', '', '', '', '<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品名称】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">通用名称：</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体检测试剂（干式免疫荧光定量法）</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">英文名称：</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP test</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【预测用途】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">用于检测人体全血样本中</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）的浓度。临床上主要用于心力衰竭的辅助诊断。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【检测原理】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">本产品采用免疫荧光定量检测技术、免疫层析法。用于检测人体全血样本中</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）的含量。将血液样本和样品缓冲液在缓冲液瓶中进行混合，缓冲液中的荧光标记抗</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）抗体和血液中的</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）抗原结合形成抗原抗体复合物。当混合物样本被加入到检测卡的加样孔后，在层析作用下沿着硝酸纤维素膜向前扩散，该复合物被固定在检测线上的</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）抗体捕获，血液样本中的</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）越多，检测线上聚集的复合物越多，荧光抗体的信号强度反映了被捕获的</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）数量，通过干式免疫荧光定量分析仪处理，能够检测血液样本中</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）的浓度。分析仪默认的</span><span style=\"font-size:18px;line-height:2;\">N</span><span style=\"font-size:18px;line-height:2;\">末端脑利钠肽前体（</span><span style=\"font-size:18px;line-height:2;\">NT-proBNP</span><span style=\"font-size:18px;line-height:2;\">）检测结果用</span><span style=\"font-size:18px;line-height:2;\">pg/mL </span><span style=\"font-size:18px;line-height:2;\">做单位。</span> \r\n</p>', '<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品参数】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">简单快速：操作简单，检测只需</span><span style=\"font-size:18px;line-height:2;\">10min</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">线性范围宽：</span><span style=\"font-size:18px;line-height:2;\">100-50000pg/mL &nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">正常参考值：</span><span style=\"font-size:18px;line-height:2;\">≤75岁：125pg/mL</span>；<span style=\"font-size:18px;line-height:2;\">＞</span><span style=\"font-size:18px;line-height:2;\">75</span><span style=\"font-size:18px;line-height:2;\">岁：</span><span style=\"font-size:18px;line-height:2;\">450</span><span style=\"font-size:18px;line-height:2;\">pg/mL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">精密度：批内</span><span style=\"font-size:18px;line-height:2;\">CV≤15%；批间CV≤20%</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">样本类型：全血</span><span style=\"font-size:18px;line-height:2;\">90μL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">包装规格：</span><span style=\"font-size:18px;line-height:2;\">25人份/盒、50人份/盒</span> \r\n</p>', '<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170629/20170629164531_57398.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">N-末端脑利钠肽前体检测试剂盒（正面）</span></a> \r\n</p>\r\n<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170629/20170629164630_72892.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">N-末端脑利钠肽前体检测试剂盒（反面）</span></a> \r\n</p>', '', '', 'N-末端脑利钠肽前体（NT-proBNP）', 2, 1, 1501553510, '', 50, 'N-末端脑利钠肽前体（NT-proBNP）是诊断、治疗及判断心力衰竭患者的重要指标。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (82, '', 30, '肌钙蛋白I检测试剂盒', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1498731961.jpg', '', '', '', '', '', '<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品名称】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">通用名称：肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">检测试剂（干式免疫荧光定量法）</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">英文名称：</span><span style=\"font-size:18px;line-height:2;\">cTnI test</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【预测用途】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">用于检测人体全血样本中肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）的浓度。临床上主要用于心肌梗死的辅助诊断。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【检测原理】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\">本产品采用免疫荧光定量检测技术、免疫层析法。用于检测人体全血样本中肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）的含量。将血液样本和样品缓冲液在缓冲液瓶中进行混合，缓冲液中的荧光标记抗肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）抗体和血液中的肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）抗原结合形成抗原抗体复合物。当混合物样本被加入到检测卡的加样孔后，在层析作用下沿着硝酸纤维素膜向前扩散，该复合物被固定在检测线上的肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）抗体捕获，血液样本中的肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）越多，检测线上聚集的复合物越多，荧光抗体的信号强度反映了被捕获的肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）数量，通过干式免疫荧光定量分析仪处理，能够检测血液样本中肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）的浓度。分析仪默认的肌钙蛋白</span><span style=\"font-size:18px;line-height:2;\">I</span><span style=\"font-size:18px;line-height:2;\">（</span><span style=\"font-size:18px;line-height:2;\">cTnI</span><span style=\"font-size:18px;line-height:2;\">）检测结果用</span><span style=\"font-size:18px;line-height:2;\">ng/mL </span><span style=\"font-size:18px;line-height:2;\">做单位。</span> \r\n</p>', '<p>\r\n	<span style=\"font-size:14px;\"><strong> </strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:18px;line-height:2;\"><strong>【产品参数】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">简单快速：操作简单，检测只需</span><span style=\"font-size:18px;line-height:2;\">15min</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">线性范围宽：</span><span style=\"font-size:18px;line-height:2;\">0.10-50ng/mL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">正常参考值：</span><span style=\"font-size:18px;line-height:2;\">cTnI＜0.3ng/mL &nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">精密度：批内</span><span style=\"font-size:18px;line-height:2;\">CV≤15%；批间CV≤20%</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">样本类型：全血</span><span style=\"font-size:18px;line-height:2;\">75μL</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;line-height:2;\">包装规格：</span><span style=\"font-size:18px;line-height:2;\">25人份/盒、50人份/盒</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"></span> \r\n</p>', '<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170629/20170629181920_94737.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">肌钙蛋白I检测试剂盒（正面）</span></a> \r\n</p>\r\n<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170629/20170629181951_14195.pdf\" target=\"_blank\"><span style=\"color:#337FE5;\">肌钙蛋白I检测试剂盒（反面）</span></a> \r\n</p>', '', '', '肌钙蛋白I（cTnI）', 2, 1, 1501553501, '', 50, '肌钙蛋白I（cTnI）是最理想的心肌梗死标志物，急性心肌梗死（AMI）诊断的金标准。', '', '', 0, 0);
