INSERT INTO `mf_goods` VALUES (50, '', 23, 'Stuart凝胶培养基拭子', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1491892128.jpg', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">IMPROSWAB&reg;微生物采集转运系统为微生物标本采集、保存、转运提供专业化的最佳整体解决方案，确保微生物的性状、生理活性等在分析前得到有效保护，提高诊断结果的准确性。同时简化医护人员的操作，保护医护人员免受生物污染。目前产品分为无培养基、带培养基拭子和样本保存液。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:18px;\">&nbsp;</span></b> \r\n</p>\r\n<span style=\"font-size:18px;\">【产品性能】管体透明，可见管体内棉签，且无肉眼可见的杂物，棉头柔软富有弹性、白色、无色斑、无污点、无异味；棉杆光滑、与皮肤接触没有毛刺感。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【产品结构】</span><span style=\"font-size:18px;\">YP/S0011(Stuart</span><span style=\"font-size:18px;\">凝胶培养基拭子）主要由管体、</span><span style=\"font-size:18px;\">Stuart</span><span style=\"font-size:18px;\">凝胶培养基、盖子、人造纤维棉头以及棉签杆组成。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【适用范围】</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 1. </span></span><span style=\"font-size:18px;\">可用于人体多部位的微生物样品收集、运送：眼耳鼻咽喉部、各种伤口分泌物、肛肠、泌尿生殖器分泌物等。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 2. </span></span><span style=\"font-size:18px;\">妇女白带检查标本采集：适用于淋球菌、链球菌、大肠杆菌的储存和转运。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 3. </span></span><span style=\"font-size:18px;\">含有硫代乙醇酸钠具有抗氧化的作用。</span><span><br />\r\n</span><span style=\"font-size:18px;\">【使用说明】</span><span> <br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 1. </span></span><span style=\"font-size:18px;\">在单包装袋撕裂指示处撕开单包装。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 2. </span></span><span style=\"font-size:18px;\">用带有盖子的棉签在病灶部位采集微生物标本。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 3. </span></span><span style=\"font-size:18px;\">把装有培养运送介质的容器盖子移去。</span><span><br />\r\n<span style=\"font-size:18px;\">&nbsp; &nbsp; 4. </span></span><span style=\"font-size:18px;\">把取样后的带有盖子的棉签插入管体内，盖