INSERT INTO `mf_goods` VALUES (63, '', 29, 'iLISA2医院智能采血管理系统', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1497436163.jpg', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:18px;\">项目适用范围</span></strong>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">医院智能采血管理系统是一个以防止血液标本差错、提升病人满意度、避免医患纠纷为目的，打造国际标准化采血流程，实现实验室标本分析前质量控制的智能化解决方案。是以采血管贴标分配系统为核心，排队管理系统为辅助，再结合为医院本身已有的HIS/LIS系统所准备的数据接口系统所组成，该项目适用于门诊、体检中心、急诊、护士站等。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:18px;\">系统组成</span></strong>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">iLISA2医院智能采血管理系统由“病人自助登记排队系统”、“中央调度系统”和“iLISA2智能选管贴标系统”组成。</span><span style=\"font-size:18px;line-height:1.5;\"></span><img src=\"/Uploads/attached/image/20170706/20170706114242_29433.png\" alt=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"><strong>产品优势</strong></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">加载试管更方便，原包装直接上机</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">备管速度快</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">机械手取管方式，更安全</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">贴心的应急系统，确保意外零可能</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">个性化定制软件，</span><span style=\"font-family:宋体;font-size:18px;\">与</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">HIS&amp;LIS</span><span style=\"font-family:宋体;font-size:18px;\">系</span><span style=\"font-family:宋体;font-size:18px;\">统对接</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">全球用户使用，验证系统稳定性</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Wingdings;font-size:18px;\">Ø</span><span style=\"font-family:\'Times New Roman\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;font-size:18px;\">专人专机驻院式服务</span>\r\n</p>', '<div style=\"text-align:center;\">\r\n	<img src=\"/Uploads/attached/image/20170614/20170614182759_73378.jpg\" alt=\"\" /> \r\n</div>', '<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170425/20170425105743_50176.pdf\" target=\"_blank\"><strong><span style=\"color:#000000;\">iLISA2产品彩页.pdf</span></strong></a>', '', '', 'iLISA2医院智能采血管理系统', 2, 1, 1499312791, '', 52, 'IMPROReady®医院智能采血管理系统由采血管贴标分配系统、排队叫号系统和软件系统组成，并与医院现有信息系统（HIS、LIS、ERM）相连，组成了集智能选管、标签打印、粘贴与分装为一体的自动化血液标本收集系统。涵盖了智能信息识别，一体化作业，超强处理能力，人性化设计四大创新功能，把传统繁杂的采血流程简化成3步，护士不再参与繁琐的备管过程，并避免患者与采血管直接接触，有效提升了采血精准性和作业效率。更能避免人为失误，防止因血标本差错导致的误诊、漏诊，推动整个诊疗过程的精准、高效运作。', '', '', 0, 0);
INSERT INTO `mf_goods` VALUES (64, '', 30, '干式荧光免疫分析仪QT-200', '', '', '', '', '', '', '', 'Uploads/goods/thumb_img/goodscat_1498787766.jpg', '', '', '', '', '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"><span><strong><span style=\"font-size:18px;\">名称：</span></strong><span style=\"font-size:18px;\">干式荧光免疫分析仪</span></span></span><span style=\"font-family:宋体;font-size:18px;\">QT-200</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:18px;\"><span style=\"font-size:18px;\"><strong>特点：</strong></span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"><span style=\"font-size:18px;\">智能经济：</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">寸彩色触摸屏，多语言友好界面，内置电池</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:18px;\"><span style=\"font-size:18px;\">简单便利：射频读卡，外接条码枪，内置热敏打印机</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:18px;\"><span style=\"font-size:18px;\">准确度高：重复性和精密度高</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:18px;\"><span style=\"font-size:18px;\">功能众多：支持多种标志物检测，满足不同科室检测需求</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"><span style=\"font-size:18px;\">高通量运行：三个独立通道，检测速度达</span><span style=\"font-size:18px;\">720</span><span style=\"font-size:18px;\">个测试</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">小时</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"><span style=\"font-size:18px;\">数据管理功能强大：数据自动存档，存储量十万条以上，支持数据传输，轻松连接</span><span style=\"font-size:18px;\">HIS</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">LIS</span><span style=\"font-size:18px;\">系统，支持</span><span style=\"font-size:18px;\">WiFi</span><span style=\"font-size:18px;\">连接，实现数据多科室共享</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5000pt;\"><span><strong><span style=\"font-size:18px;\">应用范围：</span></strong><span style=\"font-size:18px;\">广泛应用于各类医院的检验科、心内科、急诊科、</span></span><span style=\"font-size:18px;\">ICU</span><span style=\"font-size:18px;\">、肿瘤科、肾内科、儿科、内分泌科、妇科、老年科、呼吸科、胸外科、消化科、泌尿外科等科室</span></span><span style=\"font-family:宋体;font-size:10.5000pt;\"></span> \r\n</p>', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:18px;\"><strong>1&nbsp;</strong></span><span style=\"line-height:1.5;\"><strong><span style=\"font-size:18px;\">产品名称：</span></strong><span style=\"font-size:18px;\">干式荧光免疫分析仪</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:18px;\"><strong>2&nbsp;</strong></span><span style=\"line-height:1.5;font-size:18px;\"><strong>型号：</strong></span><span style=\"line-height:1.5;font-size:18px;\">QT-200</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:18px;\"><strong>3&nbsp;</strong></span><span style=\"line-height:1.5;\"><strong><span style=\"font-size:18px;\">适用范围：</span></strong><span style=\"font-size:18px;\">本产品采用荧光免疫层析定量检测的方法，与本企业已获医疗器械注册证书的相应荧光免疫层析定量试剂盒产</span></span><span style=\"line-height:1.5;font-size:18px;\">品</span><span style=\"line-height:1.5;font-size:18px;\">配套使用，用于医疗机构检测人体的</span><span style=\"line-height:1.5;font-size:18px;\">相应</span><span style=\"line-height:1.5;font-size:18px;\">样本。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-size:18px;\"><strong>4&nbsp;</strong></span><span style=\"line-height:1.5;font-size:18px;\"><strong>仪器基</strong></span><span style=\"line-height:1.5;font-size:18px;\"><strong>本</strong></span><span style=\"line-height:1.5;font-size:18px;\"><strong>参数</strong></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.1 &nbsp;</span><span style=\"font-size:18px;\">仪器基本参数尺寸：长</span><span style=\"font-size:18px;\">30╳宽24╳高15cm 所占面积：约 （30×24） cm2</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.2 &nbsp;</span><span style=\"font-size:18px;\">重量：</span><span style=\"font-size:18px;\">4.23Kg</span>\r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.3 &nbsp;</span><span style=\"font-size:18px;\">打印机：内置热敏打印机</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.4 &nbsp;</span><span style=\"font-size:18px;\">液晶显示屏：触摸电阻屏</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.5 &nbsp;</span><span style=\"font-size:18px;\">工作环境：温度</span><span style=\"font-size:18px;\">5℃～40℃，相对湿度不大于95%，避免阳光直射和机械颤动。</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.6 &nbsp;</span><span style=\"font-size:18px;\">测试通道：三通道完全独立运行</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.7 &nbsp;</span><span style=\"font-size:18px;\">数据储存：超过</span><span style=\"font-size:18px;\">10万条测试记录</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.8&nbsp;</span><span style=\"font-size:18px;\">独立质控测试页面</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.9 支持HIS/LIS系统连接</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:18.0000pt;text-indent:-18.0000pt;\">\r\n	<span style=\"font-size:18px;\">&nbsp; 4.10&nbsp;</span><span style=\"font-size:18px;\">仪器性能重复性测定结果的变异系数</span><span style=\"font-size:18px;\">CV≤10%。</span> \r\n</p>', '<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/attached/file/20170517/20170517170612_91653.pdf\" target=\"_blank\">免疫荧光定量分析平台彩页</a> \r\n</p>', '', '', '干式荧光免疫分析仪', 2, 1, 1501486497, '', 49, '本产品采用荧光免疫层析定量检测的方法，与本企业已获医疗器械注册证书的相应荧光免疫层析定量试剂盒产品配套使用，用于医疗机构检测人体的相应样本。', '', '', 0, 0);
