INSERT INTO `mf_information` VALUES (70, 'Merry Christmas', 100, 1511971200, '活动目标   \r\n1、了解西方的传统节目——圣诞节。     \r\n2、体验与老师、父母在一起欢度节日的幸福和快乐。   \r\n3、培养幼儿能勇敢的在集体面前展示和表现自己。积极主动地参加集体活动 。  \r\n4、通过亲子游戏增进幼儿与家长之间的情感交流。', 'Uploads/information/thumb_img/2017-11-30/thumb_5a1fb2e497fa3.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-11-30/original_5a1fb2e497fa3.jpg', 1, '');
