INSERT INTO `mf_information` VALUES (72, '《欢度圣诞节》方案', 100, 1511971200, '幼儿园大班主题区域活动策划方案', 'Uploads/information/thumb_img/2017-11-30/thumb_5a1fba3f2e284.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-11-30/original_5a1fba3f2e284.jpg', 1, '');
INSERT INTO `mf_information` VALUES (73, '母亲节主题活动', 100, 1512057600, '活动来源：在五月的第二个星期天，是一个传达爱的日子——母亲节，但随着文明的进步，这种根深的情感，却被快节奏的生活慢慢代替，人们不再有时间去大声的说出自己的爱，渐渐的人们也就不愿意去表达自己的爱，至少，当母亲节这样一个温馨的节日，人们能够想起，却也不知道用什么样的办法来表自己的情感，母爱是世界上最伟大的爱，是一个人生命的源泉，对于幼小的孩童而言，因为这些爱，生活才变得如此的柔软和温馨，因为这样的爱，眼中的世界是如此的美好，因此，我们开展了以“爱。言”为主题的母亲节活动。', 'Uploads/information/thumb_img/2017-12-01/thumb_5a20d3cd54033.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-01/original_5a20d3cd54033.jpg', 1, '');
INSERT INTO `mf_information` VALUES (74, '中秋节主题活动', 100, 1512316800, '活动目标： 　　1、通过活动，让幼儿初步了解中秋节的来历，知道中秋节是我国传统的团圆节。 　　2、体验与同伴老师一起过节的快乐。 　　活动准备：发动幼儿自带水果和月饼 　　活动形式：班级集体活动 　　活动流程：介绍节日——欣赏故事——谈话品尝', 'Uploads/information/thumb_img/2017-12-04/thumb_5a2512276c89d.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-04/original_5a2512276c89d.jpg', 1, '');
INSERT INTO `mf_information` VALUES (75, '万圣节游戏', 100, 1512403200, '活动一:打怪兽\r\n目的:训练幼儿手眼协调能力和幼儿的竞争意识。\r\n玩法:教师事先准备篮子，分别贴上怪兽的样子，分组，请幼儿用球丢向怪兽框中，打怪兽，最后看哪组的幼儿丢的乒乓球最多，可获盖一个小印章。', 'Uploads/information/thumb_img/2017-12-05/thumb_5a2663563ae2f.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-05/original_5a2663563ae2f.jpg', 0, '');
INSERT INTO `mf_information` VALUES (76, '幼儿园托班手指游戏：大拇指', 100, 1512489600, '手指是孩子最原始的玩具，但孩子不清楚每个手指的名称。在小小的手指尖套上可爱的圆锥帽，跟着教师唱唱、说说、玩玩——孩子在这样的活动中不仅能认识五个手指，初步了解它们的名称，还锻炼了手指肌肉的灵活性。\r\n      ——选自《2～3岁婴幼儿教养活动(教师参考用书)》，第48页\r\n', 'Uploads/information/thumb_img/2017-12-06/thumb_5a27b84056104.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-06/original_5a27b84056104.jpg', 0, '');
INSERT INTO `mf_information` VALUES (77, '幼儿园小班美术活动教案：蓝和黄', 100, 1512576000, '活动目标\r\n\r\n　　1、学习正确的拓印技能，能用简单的工具拓印。\r\n\r\n　　2、认识黄色、蓝色和绿色，知道蓝色和黄色相加会变成绿色。\r\n\r\n　　3、乐意探索色彩知识，体验自由创作的乐趣。', 'Uploads/information/thumb_img/2017-12-07/thumb_5a290b3f992ee.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-07/original_5a290b3f992ee.jpg', 0, '');
INSERT INTO `mf_information` VALUES (79, '幼儿园大班手工制作活动：制作贺卡', 100, 1512662400, '活动目标　　 \r\n1、激发幼儿参与设计、制作的兴趣，培养幼儿做事的自信心。 　　 \r\n2、引导幼儿选择自己喜欢的方式设计并制作贺卡，能用自己的语言向老师说出祝福的话。 \r\n3、使幼儿初步了解贺卡的用途，制作材料和方法。', 'Uploads/information/thumb_img/2017-12-08/thumb_5a2a0cd5e7041.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-08/original_5a2a0cd5e7041.jpg', 0, '');
INSERT INTO `mf_information` VALUES (80, '幼儿园数学区自制手工玩教具：插插乐', 100, 1512921600, '“插插乐”数字1-10的排序教学目标：\r\n\r\n认识数字1——10，锻炼幼儿插的动作练习。', 'Uploads/information/thumb_img/2017-12-11/thumb_5a2e2a09ddad8.jpg', 1, 0, 0, 0, 2, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-11/original_5a2e2a09ddad8.jpg', 0, '');
INSERT INTO `mf_information` VALUES (81, '幼儿园父亲节活动策划', 100, 1513094400, '父亲的角色在孩子的成长道路上有着不可或缺的作用，在现代家庭中，爸爸在孩子心目中的形象是繁忙、威严，孩子与爸爸接触交流的机会甚少。\r\n\r\n', 'Uploads/information/thumb_img/2017-12-13/thumb_5a30ddd76c7c5.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-13/original_5a30ddd76c7c5.jpg', 1, '');
INSERT INTO `mf_information` VALUES (82, '用乒乓球制作七星瓢虫的方法', 100, 1513180800, '七星瓢虫身体像半个圆球，头黑黑的、翅膀是橘色的，因为翅膀有七个黑色的圆点点，所以人们叫它七星瓢虫 。七星瓢虫为益虫，被人们称为“活农药”，俗称“花大姐”', 'Uploads/information/thumb_img/2017-12-14/thumb_5a32282871b71.jpg', 1, 0, 0, 0, 4, 0, 0, 0, 0, 'Uploads/information/thumb_img/2017-12-14/original_5a32282871b71.jpg', 1, 'Uploads/download/2018-03-21/5ab20d37bcf33.jpg');
