INSERT INTO `mf_information_content` VALUES (45, 33, 0, 'Uploads/information/thumb_img/2017-11-27/original_5a1bdbf23579c.jpg', '厨师帽：取三张A4的白卡纸粘贴成一整张，然后对折，沿对折线剪出等距离的条状，最后将两端用胶水或订书机固定为头围大小即可。\r\n', 'Uploads/information/thumb_img/2017-11-27/thumb_5a1bdbf23579c.jpg');
INSERT INTO `mf_information_content` VALUES (46, 34, 0, 'Uploads/information/thumb_img/2017-11-27/original_5a1bddfecf6da.jpg', '雪糕棒的小创意，挂在墙面上，绝对吸引眼球。', 'Uploads/information/thumb_img/2017-11-27/thumb_5a1bddfecf6da.jpg');
