INSERT INTO `mf_information_content` VALUES (81, 41, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1cc974e3a81.jpg', '准备材料：彩纸、剪刀、胶水、透明纸', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cc974e3a81.jpg');
INSERT INTO `mf_information_content` VALUES (82, 41, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1cc97504d0a.jpg', '制作步骤：在黑色纸上剪下手套的轮廓，粘贴在透明纸上。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cc97504d0a.jpg');
INSERT INTO `mf_information_content` VALUES (83, 41, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1cc9751b5c6.jpg', '将其他颜色的彩纸剪成小方形，粘在“手套”上，颜色搭配可以让幼儿自由发挥哦~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cc9751b5c6.jpg');
INSERT INTO `mf_information_content` VALUES (84, 41, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1cc9753d24e.jpg', '粘贴完后，沿着手套的轮廓剪下来。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cc9753d24e.jpg');
INSERT INTO `mf_information_content` VALUES (85, 41, 4, 'Uploads/information/thumb_img/2017-11-28/original_5a1cc9755193e.jpg', '把小朋友们做好的手套展示在窗户上吧！是不是感觉暖暖的！', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cc9755193e.jpg');
INSERT INTO `mf_information_content` VALUES (86, 42, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccb11a543b.jpg', '准备材料：卡纸、画笔、棉花、蜡笔、胶水、线', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccb11a543b.jpg');
INSERT INTO `mf_information_content` VALUES (87, 42, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccb11c3f60.jpg', '制作步骤：在卡纸上画下帽子的轮廓。\r\n用蜡笔给帽子涂色。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccb11c3f60.jpg');
INSERT INTO `mf_information_content` VALUES (88, 42, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccb11db15a.jpg', '在帽子的下边缘和顶上粘上棉花，然后用线挂起来，教师变得多温馨呀~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccb11db15a.jpg');
INSERT INTO `mf_information_content` VALUES (89, 43, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccba466b61.jpg', '准备材料：卫生纸、剪刀、彩笔、订书机', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccba466b61.jpg');
INSERT INTO `mf_information_content` VALUES (90, 43, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccba478fa1.jpg', '制作步骤：按照上图，在卫生纸上剪下几个大圆形、\r\n\r\n小圆形、V字圆形，然后用订书机订在一起。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccba478fa1.jpg');
INSERT INTO `mf_information_content` VALUES (91, 43, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccba4a17e2.jpg', '给“天使”涂上颜色，冬日天使就带着好运悄悄滴来啦~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccba4a17e2.jpg');
INSERT INTO `mf_information_content` VALUES (92, 44, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccc3b72393.jpg', '准备材料：卡纸、剪刀、胶水、毛球、亮片', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccc3b72393.jpg');
INSERT INTO `mf_information_content` VALUES (93, 44, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccc3b8f2fb.jpg', '制作步骤：在白色卡纸上剪下几个圆圈。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccc3b8f2fb.jpg');
INSERT INTO `mf_information_content` VALUES (94, 44, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccc3baf69d.jpg', '在卡纸上粘上一个框，再用白色卡纸一边剪成波浪形，作为积雪，\r\n\r\n把白色圆形粘在在积雪上，并用毛球和亮片装饰，做成雪人，贺卡搞定。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccc3baf69d.jpg');
INSERT INTO `mf_information_content` VALUES (95, 45, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccc92602de.jpg', '准备材料：废旧圆形瓶、染料、画笔、布条', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccc92602de.jpg');
INSERT INTO `mf_information_content` VALUES (96, 46, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb35eb82.jpg', '准备材料：纸杯、不织布、毛根扭扭棒、胶水、毛球\r\n\r\n制作步骤：在不织布上剪下长条形，两端剪成流苏状，作为围巾。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb35eb82.jpg');
INSERT INTO `mf_information_content` VALUES (97, 46, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb385e3b.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb385e3b.jpg');
INSERT INTO `mf_information_content` VALUES (98, 46, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb39c6e2.jpg', '把围巾围在纸杯上。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb39c6e2.jpg');
INSERT INTO `mf_information_content` VALUES (99, 46, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb3b2cc3.jpg', '在橙色不织布上剪下三角形，作为鼻子。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb3b2cc3.jpg');
INSERT INTO `mf_information_content` VALUES (100, 46, 4, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb3c8403.jpg', '把鼻子粘在纸杯上，并在纸杯顶端粘上毛根扭扭棒。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb3c8403.jpg');
INSERT INTO `mf_information_content` VALUES (101, 46, 5, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb3de527.jpg', '在毛根扭扭棒两端粘上毛球。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb3de527.jpg');
INSERT INTO `mf_information_content` VALUES (102, 46, 6, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccdb4008c4.jpg', 'OK，听着音乐的小雪人就做好啦~~~\r\n\r\n雪人的创意有很多种，老师们可以带着孩子开启想象力，尽情创意，下面展示几款其他的小雪人：', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccdb4008c4.jpg');
INSERT INTO `mf_information_content` VALUES (103, 46, 7, 'Uploads/information/thumb_img/2017-11-28/original_5a1cce56438df.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cce56438df.jpg');
INSERT INTO `mf_information_content` VALUES (104, 46, 8, 'Uploads/information/thumb_img/2017-11-28/original_5a1cce5664058.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cce5664058.jpg');
INSERT INTO `mf_information_content` VALUES (105, 46, 9, 'Uploads/information/thumb_img/2017-11-28/original_5a1cce5689a29.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cce5689a29.jpg');
INSERT INTO `mf_information_content` VALUES (106, 46, 10, 'Uploads/information/thumb_img/2017-11-28/original_5a1cce56a6fdc.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cce56a6fdc.jpg');
INSERT INTO `mf_information_content` VALUES (107, 47, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef5260b3.jpg', '准备材料：染料、胶水、荧光粉、线', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef5260b3.jpg');
INSERT INTO `mf_information_content` VALUES (108, 47, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef54c53b.jpg', '制作步骤：在树枝上涂上染料。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef54c53b.jpg');
INSERT INTO `mf_information_content` VALUES (109, 47, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef56086e.jpg', '再涂上胶水，撒上荧光粉。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef56086e.jpg');
INSERT INTO `mf_information_content` VALUES (110, 47, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef57483a.jpg', '然后将三根树枝交叉粘在一起，挂起来，这可是不会融化的雪花哦~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef57483a.jpg');
INSERT INTO `mf_information_content` VALUES (111, 47, 4, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef59abb5.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef59abb5.jpg');
INSERT INTO `mf_information_content` VALUES (112, 47, 5, 'Uploads/information/thumb_img/2017-11-28/original_5a1ccef5cb910.jpg', '如果树枝不好收集，我们可以把棉棒剪断，涂成白色，\r\n\r\n粘在蓝色卡纸上，然后沿着轮廓剪下来，这样挂起来也不错哦~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1ccef5cb910.jpg');
INSERT INTO `mf_information_content` VALUES (113, 48, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd2180d879.jpg', '准备材料：透明塑料杯子、打印纸、铅笔、蜡笔、记号笔、剪刀、透明胶带、LED灯。\r\n\r\n制作步骤： 拿一个透明玻璃杯在卡纸上滚动，边滚边用铅笔描线', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd2180d879.jpg');
INSERT INTO `mf_information_content` VALUES (114, 48, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd2182a1ea.jpg', '画出辅助线\r\n\r\n裁剪下来并折成扇形', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd2182a1ea.jpg');
INSERT INTO `mf_information_content` VALUES (115, 48, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd21840fb4.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd21840fb4.jpg');
INSERT INTO `mf_information_content` VALUES (116, 48, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd218584d9.jpg', '', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd218584d9.jpg');
INSERT INTO `mf_information_content` VALUES (117, 48, 4, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd2186f2da.jpg', '在折叠好的纸上用剪刀剪几下，就像剪雪花一样', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd2186f2da.jpg');
INSERT INTO `mf_information_content` VALUES (118, 48, 5, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd21886172.jpg', '打开折纸，用蜡笔加些可爱的修饰，小天使裙子做好啦。\r\n\r\n接着再剪一个可爱的脑袋和一对翅膀。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd21886172.jpg');
INSERT INTO `mf_information_content` VALUES (119, 48, 6, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd2189f2f9.jpg', '把剪好的裙子、脸、翅膀粘在透明玻璃杯上。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd2189f2f9.jpg');
INSERT INTO `mf_information_content` VALUES (120, 48, 7, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd218b71b9.jpg', '在每个杯子里放 1-2 个 LED 小灯，天使小灯就能发光了哦。', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd218b71b9.jpg');
INSERT INTO `mf_information_content` VALUES (121, 49, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd29839a64.jpg', '圣诞节快到了，想要送出不一样的礼物吗？\r\n\r\n下面这个小卡摆在教室桌面上甚是可爱呢', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd29839a64.jpg');
INSERT INTO `mf_information_content` VALUES (122, 49, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd298565ee.jpg', '准备材料：小毛线球、小夹子、颜料、卷纸筒、卡纸', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd298565ee.jpg');
INSERT INTO `mf_information_content` VALUES (123, 49, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd29873017.jpg', '制作步骤：剪一截卷纸筒作底部，深蓝色卡纸剪一个圆形。\r\n\r\n用小夹子夹一个棉球做颜料刷，然后就可以让孩子们拿这个\r\n\r\n小刷子在卡纸上画小雪人和雪花啦！', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd29873017.jpg');
INSERT INTO `mf_information_content` VALUES (124, 49, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1cd2988a3a3.jpg', '做起来这么简单，有了这个创意就不用愁了', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cd2988a3a3.jpg');
INSERT INTO `mf_information_content` VALUES (125, 50, 0, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf098f46d.jpg', '这个阅读区的环境布置与娃娃家相结合的，真的温馨的不行不行的，是否激发了你的灵感呢~~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf098f46d.jpg');
INSERT INTO `mf_information_content` VALUES (126, 50, 1, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf09a46a3.jpg', '清新的色调加上有学习气息的小黑板一样的东西，虽然孩子们不认识字但是给大家一种我来到这里是来看书的感觉哦~~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf09a46a3.jpg');
INSERT INTO `mf_information_content` VALUES (127, 50, 2, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf09bb9b4.jpg', '这一款地方不大，好在还是比较童趣，只是稍微有点凌乱的感觉，如果再改进一些就更好了了，但细节的布置还是值得参考的！', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf09bb9b4.jpg');
INSERT INTO `mf_information_content` VALUES (128, 50, 3, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf09d0592.jpg', '这个布置能激发孩子们的想象力，木质的墙面加上木质的小桌子，真的可爱的不行不行的~~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf09d0592.jpg');
INSERT INTO `mf_information_content` VALUES (129, 50, 4, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf09e924d.jpg', '在墙的角落里放阅读区，加上这个粉红色的墙壁真的很温暖又有安全感，相对来说比较简单，不知道实际运用孩子们是否会捧场？', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf09e924d.jpg');
INSERT INTO `mf_information_content` VALUES (130, 50, 5, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf0a0b47d.jpg', '这样大的阅读区，孩子们更喜欢在这里玩耍，可以添加一些其他的装饰，使空间丰富起来，说不定更能吸引孩子们！', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf0a0b47d.jpg');
INSERT INTO `mf_information_content` VALUES (131, 50, 6, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf0a29500.jpg', '这样的小书屋也是极好的呢~~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf0a29500.jpg');
INSERT INTO `mf_information_content` VALUES (132, 50, 7, 'Uploads/information/thumb_img/2017-11-28/original_5a1cdf0a41a4e.jpg', '这个也是不错的哦~~', 'Uploads/information/thumb_img/2017-11-28/thumb_5a1cdf0a41a4e.jpg');
