INSERT INTO `mf_information_content` VALUES (183, 61, 0, 'Uploads/information/thumb_img/2017-11-29/original_5a1e2e2a46d8e.jpg', '小时候的跳房子，在室内也能玩起来。', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e2e2a46d8e.jpg');
INSERT INTO `mf_information_content` VALUES (184, 61, 1, 'Uploads/information/thumb_img/2017-11-29/original_5a1e2e2a6ec08.jpg', '小年龄的小朋友不能理解太复杂的规则，那么我们可以多给一些暗示，简单一点', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e2e2a6ec08.jpg');
