INSERT INTO `mf_information_content` VALUES (191, 62, 5, 'Uploads/information/thumb_img/2017-11-29/original_5a1e2fef2ce07.jpg', '两把椅子一根绳子就能组成球场', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e2fef2ce07.jpg');
INSERT INTO `mf_information_content` VALUES (192, 62, 6, 'Uploads/information/thumb_img/2017-11-29/original_5a1e2fef4ee57.jpg', '这个真是考验孩子的肺活量', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e2fef4ee57.jpg');
INSERT INTO `mf_information_content` VALUES (193, 63, 0, 'Uploads/information/thumb_img/2017-11-29/original_5a1e65365c702.jpg', '推小车\r\n\r\n锻炼孩子上臂力量的绝佳运动。两两合作，还能分组竞赛。', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e65365c702.jpg');
INSERT INTO `mf_information_content` VALUES (194, 63, 1, 'Uploads/information/thumb_img/2017-11-29/original_5a1e653672b67.jpg', '夹球快走\r\n\r\n配合默契还要速度够快哦。', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e653672b67.jpg');
INSERT INTO `mf_information_content` VALUES (195, 63, 2, 'Uploads/information/thumb_img/2017-11-29/original_5a1e653691dc0.jpg', '彩虹伞\r\n\r\n可以在伞下开展各种好玩的游戏：许多小鱼游来了，让我们一起抓住它们吧。', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e653691dc0.jpg');
INSERT INTO `mf_information_content` VALUES (196, 63, 3, 'Uploads/information/thumb_img/2017-11-29/original_5a1e6536a967b.jpg', '', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e6536a967b.jpg');
INSERT INTO `mf_information_content` VALUES (197, 63, 4, 'Uploads/information/thumb_img/2017-11-29/original_5a1e6536c1989.jpg', '可以手拉手围成圈，变出许多小网洞，让扮演“小鱼”的孩子们穿梭其中，歌曲停，网落下，看看我们网住了几条小鱼呢？', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e6536c1989.jpg');
INSERT INTO `mf_information_content` VALUES (198, 63, 5, 'Uploads/information/thumb_img/2017-11-29/original_5a1e6536d8622.jpg', '儿童瑜伽是很适合作为室内运动的结尾和其他活动的过度环节。\r\n\r\n儿童瑜伽的动作比较简单，教师带领孩子一起做的同时，可以帮助运动兴奋后的孩子调整呼吸，慢慢进入安静的舒缓的情绪，回复到适合室内生活学习的状态。', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e6536d8622.jpg');
INSERT INTO `mf_information_content` VALUES (199, 63, 6, 'Uploads/information/thumb_img/2017-11-29/original_5a1e653700d58.jpg', 'S形走\r\n\r\nS形走，最先到达目的地的运球者胜出', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e653700d58.jpg');
INSERT INTO `mf_information_content` VALUES (200, 63, 7, 'Uploads/information/thumb_img/2017-11-29/original_5a1e65371e3e6.jpg', '钓鱼\r\n\r\n规定时间内，钓鱼者最多胜出', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e65371e3e6.jpg');
INSERT INTO `mf_information_content` VALUES (201, 63, 8, 'Uploads/information/thumb_img/2017-11-29/original_5a1e653741827.jpg', '配对游戏\r\n\r\n最少的时间内杯子和纸板上的数字完成配对', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e653741827.jpg');
INSERT INTO `mf_information_content` VALUES (202, 63, 9, 'Uploads/information/thumb_img/2017-11-29/original_5a1e65376a2cf.jpg', '把球投入杯子内\r\n\r\n规定时间内，投入纸杯的球最多者胜出', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e65376a2cf.jpg');
INSERT INTO `mf_information_content` VALUES (203, 63, 10, 'Uploads/information/thumb_img/2017-11-29/original_5a1e6537842ba.jpg', '投射\r\n\r\n规定时间内，谁投入的棉球/兵乓球最多', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e6537842ba.jpg');
INSERT INTO `mf_information_content` VALUES (204, 63, 11, 'Uploads/information/thumb_img/2017-11-29/original_5a1e65379d53b.jpg', '模拟篮球投球\r\n\r\n模拟篮球的投球，每个杯子上有对应的获得分数', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e65379d53b.jpg');
INSERT INTO `mf_information_content` VALUES (205, 63, 12, 'Uploads/information/thumb_img/2017-11-29/original_5a1e6537bb71b.jpg', '把球吹到另外的杯子\r\n\r\n可把杯子连城直线，从头到尾，最先球吹到另外一个杯子者胜', 'Uploads/information/thumb_img/2017-11-29/thumb_5a1e6537bb71b.jpg');
