INSERT INTO `mf_kindergarten` VALUES (17, 100, '黄小姐', '白云区旭日幼儿园属于民办教育机构', 1, '13826116082', 6, 76, 695, 1519833600, 2, '白云区新市镇', '广州白云区X日幼儿园', 1, 1546272000, 0);
INSERT INTO `mf_kindergarten` VALUES (18, 100, '黄小姐', '恬静四合院式的园区总建筑面积约5000多平方米。\r\n园内配备科常室、美术室、阅览室和舞蹈室等功能室，\r\n每课室内亦配有直饮水系统、空气消毒系统及多媒体教学设备，\r\n让幼儿在安全舒适的环境中成长！', 1, '13826116082', 6, 76, 696, 1521648000, 8, '广州市 海珠区 海幢街', '广州市明X幼儿园', 1, 1546272000, 0);
INSERT INTO `mf_kindergarten` VALUES (14, 100, '黄小姐', '广州市一级幼儿园\r\n\r\n办园理念：一切为了孩子，坚持全人教育，培养走向世界的现代人\r\n\r\n办园特色：感受中华文化，体验世界风情\r\n\r\n育人目标：健康自信、活泼有礼、勇于探索、具有国际视野的世界小公民', 1, '13826116082', 6, 76, 695, 1521648000, 3, '广州市 白云山附近', '广东外语XXXX幼儿园', 1, 1543852800, 0);
INSERT INTO `mf_kindergarten` VALUES (15, 100, '黄小姐', '艺术幼儿园是全国优秀民办幼儿园、广东省十佳民办幼儿园。\r\n位于风景秀丽的白云山麓南湖边，多年来以艺术教育为特色.', 1, '13826116082', 6, 76, 698, 1521648000, 1, '广州市 白云区', ' 广州市白云区xxxx艺术幼儿园', 0, 1519833600, 0);
INSERT INTO `mf_kindergarten` VALUES (16, 100, '黄小姐', '民XXX幼儿园拥有四个各具特色、外形美观的园舍，园内建筑布局合理，设计新颖，户外活动场地宽敞，小桥流水、绿树婆娑，是“广东省绿色幼儿园”。能充分利用各种教育资源，创设了集教育与娱乐功能于一体的多个功能室（区），设施设备齐全，玩具材料丰富，为幼儿的游戏、探索和发展提供了有利的条件和有力的保障。　', 1, '13826116082', 6, 76, 695, 1519833600, 1, '广州市白云区 西厂路', '民XXX幼儿园', 1, 1546272000, 0);
